import React, { useState, useEffect } from 'react';
import axios from 'axios';
import VisualizationRenderer from './VisualizationRenderer';
import flightFacts from './flightFacts';

export default function ChatInterface() {
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [currentFact, setCurrentFact] = useState('');
  const [history, setHistory] = useState(() => {
    const saved = localStorage.getItem('queryHistory');
    return saved ? JSON.parse(saved) : [];
  });

  const getRandomFact = () => {
    const randomIndex = Math.floor(Math.random() * flightFacts.length);
    return flightFacts[randomIndex];
  };

  useEffect(() => {
    localStorage.setItem('queryHistory', JSON.stringify(history));
  }, [history]);

  async function handleSubmit(e) {
    e.preventDefault();
    if (!query.trim()) return;

    setLoading(true);
    setError(null);
    setCurrentFact(getRandomFact());

    try {
      const res = await axios.post('http://localhost:5000/api/query', {
        query: query.trim()
      }, {
        timeout: 60000
      });

      const result = { ...res.data, query: query.trim() };
      setResponse(result);
      setHistory(prev => [...prev, {
        query: query.trim(),
        response: result
      }]);
    } catch (err) {
      setError(
        err.response?.data?.error ||
        err.message ||
        'Error processing your query'
      );
      console.error('Query error:', err);
    } finally {
      setLoading(false);
      setCurrentFact('');
    }
  }

  return (
    <div className="chat-interface">
      <form onSubmit={handleSubmit} className="query-form">
        <input
          type="text"
          aria-label="Flight data query input"
          placeholder="Ask about flight bookings..."
          value={query}
          onChange={e => setQuery(e.target.value)}
          disabled={loading}
        />
        <button
          type="submit"
          aria-busy={loading}
          disabled={loading}
        >
          {loading ? 'Processing...' : 'Ask'}
        </button>
      </form>

      {loading && (
        <div className="loading-indicator">
          <div className="spinner"></div>
          {currentFact && (
            <div className="fun-fact">
              ✈️ Did you know? {currentFact}
            </div>
          )}
        </div>
      )}

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}

      {response && (
        <div className="response-container">
          <VisualizationRenderer response={response} />
        </div>
      )}

      {history.length > 0 && (
        <div className="query-history">
          <h3>Previous Queries</h3>
          {history.slice().reverse().map((item, index) => (
            <div key={index} className="history-item">
              <div className="history-query">Q: {item.query}</div>
              <VisualizationRenderer response={item.response} />
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
