// src/components/ChatInterface.jsx
import React, { useState } from 'react';
import axios from 'axios';
import VisualizationRenderer from './VisualizationRenderer';

export default function ChatInterface() {
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  async function handleSubmit(e) {
    e.preventDefault();
    if (!query.trim()) return;

    setLoading(true);
    setError(null);
    setResponse(null);

    try {
      const res = await axios.post('http://localhost:5000/api/query', {
        query: query.trim()
      }, {
        timeout: 60000  // allow up to 60s for complex queries
      });

      const { data } = res;
      if (data && typeof data.type !== 'undefined' && 'data' in data) {
        setResponse(data);
      } else {
        throw new Error('Invalid response format from server.');
      }
    } catch (err) {
      setError(
        err.response?.data?.error ||
        err.message ||
        'Error processing your query.'
      );
      console.error('Query error:', err);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div style={{ maxWidth: 900, margin: 'auto', padding: 20 }}>
      <form onSubmit={handleSubmit} style={{ display: 'flex', marginBottom: 20 }}>
        <input
          type="text"
          placeholder="Ask about flight bookings..."
          value={query}
          onChange={e => setQuery(e.target.value)}
          style={{
            flex: 1,
            padding: '12px',
            fontSize: '1rem',
            border: '1px solid #ddd',
            borderRadius: '4px'
          }}
          disabled={loading}
        />
        <button
          type="submit"
          disabled={loading}
          style={{
            marginLeft: 10,
            padding: '12px 20px',
            backgroundColor: '#4CAF50',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: loading ? 'not-allowed' : 'pointer'
          }}
        >
          {loading ? 'Processing…' : 'Ask'}
        </button>
      </form>

      {loading && (
        <div style={{
          padding: '15px',
          margin: '10px 0',
          background: '#f0f0f0',
          borderRadius: '4px'
        }}>
          Processing your query…
        </div>
      )}

      {error && (
        <div style={{
          color: 'white',
          backgroundColor: '#e74c3c',
          padding: '10px',
          borderRadius: '4px',
          margin: '10px 0'
        }}>
          {error}
        </div>
      )}

      {response && (
        <VisualizationRenderer response={response} />
      )}
    </div>
  );
}
