import React from 'react';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';

export default function VisualizationRenderer({ response }) {
  if (!response) return null;

  const { type, data } = response;

  switch (type) {
    case 'string':
      return <div><pre>{data}</pre></div>;

    case 'pie': {
      const options = {
        chart: { type: 'pie' },
        title: { text: 'Distribution' },
        series: [{
          name: 'Value',
          data: data.map(d => ({ name: d.name, y: d.value })),
        }],
      };
      return <HighchartsReact highcharts={Highcharts} options={options} />;
    }

    case 'bar': {
      const categories = data.map(d => d.name);
      const values = data.map(d => d.value);
      const options = {
        chart: { type: 'bar' },
        title: { text: 'Bar Chart' },
        xAxis: { categories },
        yAxis: { title: { text: 'Value' } },
        series: [{ name: 'Value', data: values }],
      };
      return <HighchartsReact highcharts={Highcharts} options={options} />;
    }

    case 'timeseries': {
      const categories = data.map(d => d.date);
      const values = data.map(d => d.value);
      const options = {
        chart: { type: 'line' },
        title: { text: 'Time Series' },
        xAxis: { categories },
        yAxis: { title: { text: 'Value' } },
        series: [{ name: 'Value', data: values }],
      };
      return <HighchartsReact highcharts={Highcharts} options={options} />;
    }

    case 'table': {
      return (
        <table border="1" cellPadding="5" cellSpacing="0" style={{ width: '100%', marginTop: 10 }}>
          <thead>
            <tr>{data.length > 0 && Object.keys(data[0]).map((key) => <th key={key}>{key}</th>)}</tr>
          </thead>
          <tbody>
            {data.map((row, idx) => (
              <tr key={idx}>
                {Object.values(row).map((val, i) => <td key={i}>{val}</td>)}
              </tr>
            ))}
          </tbody>
        </table>
      );
    }

    default:
      return <div>Unsupported visualization type.</div>;
  }
}
