import React from 'react';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';

const colorPalette = [
  '#4361ee', '#3a0ca3', '#4895ef', '#4cc9f0', 
  '#f72585', '#7209b7', '#b5179e', '#560bad',
  '#480ca8', '#3a0ca3', '#3f37c9', '#4361ee'
];

const detectChartType = (data, query) => {
  if (!data || data.length === 0) return 'table';

  const columns = Object.keys(data[0]);
  const numCols = columns.length;

  // Time series detection
  const hasDate = columns.some(c => 
    c.toLowerCase().includes('date') || 
    c.toLowerCase().includes('time')
  );

  if (hasDate) return 'line';

  // Distribution detection
  if (query.toLowerCase().includes('distribution') || 
      query.toLowerCase().includes('percentage')) {
    return 'pie';
  }

  // Comparison detection
  if (query.toLowerCase().includes('compare') && numCols > 2) {
    return 'column';
  }

  // Default for two columns
  if (numCols === 2) {
    return query.toLowerCase().includes('trend') ? 'line' : 'column';
  }

  return 'table';
};

const prepareChartData = (data, chartType) => {
  const columns = Object.keys(data[0] || {});

  if (chartType === 'pie') {
    const [nameCol, valueCol] = columns;
    return data.map(row => ({
      name: row[nameCol],
      y: Number(row[valueCol]) || 0
    }));
  }

  if (chartType === 'line') {
    const dateCol = columns.find(c => 
      c.toLowerCase().includes('date') || 
      c.toLowerCase().includes('time')
    );
    const valueCol = columns.find(c => c !== dateCol);

    return data.map(row => ({
      x: new Date(row[dateCol]).getTime(),
      y: Number(row[valueCol]) || 0,
      name: new Date(row[dateCol]).toLocaleDateString()
    }));
  }

  // For column/bar charts
  const [nameCol, ...valueCols] = columns;
  return valueCols.map((col, i) => ({
    name: col,
    data: data.map(row => ({
      name: row[nameCol],
      y: Number(row[col]) || 0,
      color: colorPalette[i % colorPalette.length]
    }))
  }));
};

const getChartOptions = (chartType, data, query) => {
  const baseOptions = {
    title: { text: query },
    colors: colorPalette,
    accessibility: { enabled: false },
    credits: { enabled: false },
    chart: { 
      type: chartType,
      backgroundColor: 'transparent'
    },
    plotOptions: {
      series: {
        borderRadius: chartType === 'column' ? 4 : 0,
        colorByPoint: chartType !== 'line'
      },
      column: {
        grouping: false,
        shadow: false,
        borderWidth: 0
      }
    }
  };

  if (chartType === 'pie') {
    return {
      ...baseOptions,
      plotOptions: {
        pie: {
          allowPointSelect: true,
          cursor: 'pointer',
          dataLabels: {
            enabled: true,
            format: '<b>{point.name}</b>: {point.y}'
          }
        }
      },
      series: [{ data }]
    };
  }

  if (chartType === 'line') {
    return {
      ...baseOptions,
      xAxis: { type: 'datetime' },
      series: [{ data }]
    };
  }

  return {
    ...baseOptions,
    xAxis: { type: 'category' },
    series: data
  };
};

export default function VisualizationRenderer({ response }) {
  const { type, data, query } = response || {};

  if (type === 'string') {
    return <div className="response-string">{data}</div>;
  }

  if (type === 'table') {
    if (!Array.isArray(data)) {
      return <div className="error">Invalid data format</div>;
    }

    const chartType = detectChartType(data, query);
    const chartData = prepareChartData(data, chartType);
    const showChart = data.length > 0 && data.length < 1000 && chartType !== 'table';

    return (
      <div className="response-container">
        {showChart && (
          <div className="chart-container">
            <HighchartsReact
              highcharts={Highcharts}
              options={getChartOptions(chartType, chartData, query)}
            />
          </div>
        )}

        <div className="response-table">
          <table>
            <thead>
              <tr>
                {data.length > 0 && Object.keys(data[0]).map(col => (
                  <th key={col}>{col}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {data.map((row, i) => (
                <tr key={i}>
                  {Object.values(row).map((val, j) => (
                    <td key={j}>{val?.toString() || '—'}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  }

  return <div className="error">Unsupported response type</div>;
}
