import React, { useEffect } from 'react';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';

// Import the dark theme directly from Highcharts
import HC_darkUnica from 'highcharts/themes/dark-unica';

const VisualizationRenderer = ({ response }) => {
  const { type, data } = response || {};

  useEffect(() => {
    // Apply dark theme if dark mode is active
    if (document.documentElement.classList.contains('dark-mode')) {
      HC_darkUnica(Highcharts);
    } else {
      // Apply light theme options
      Highcharts.setOptions({
        colors: ['#4361ee', '#3a0ca3', '#4895ef', '#4cc9f0', '#f72585', '#7209b7'],
        chart: {
          backgroundColor: 'transparent',
          style: {
            fontFamily: 'Inter, sans-serif'
          }
        },
        title: {
          style: {
            color: '#2b2d42'
          }
        },
        legend: {
          itemStyle: {
            color: '#2b2d42'
          }
        },
        xAxis: {
          labels: {
            style: {
              color: '#2b2d42'
            }
          }
        },
        yAxis: {
          labels: {
            style: {
              color: '#2b2d42'
            }
          }
        }
      });
    }
  }, []);

  // ... rest of your component code
};

export default VisualizationRenderer;
