import React, { useState } from 'react';
import axios from 'axios';
import VisualizationRenderer from './VisualizationRenderer';

export default function ChatInterface() {
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  async function handleSubmit(e) {
    e.preventDefault();
    if (!query.trim()) return;

    setLoading(true);
    setError(null);
    setResponse(null);

    try {
      // First check backend health
      await axios.get('http://localhost:5000/api/health', { timeout: 3000 });

      // Then make the query
      const res = await axios.post('http://localhost:5000/api/query', 
        { query: query.trim() },
        { timeout: 30000 }
      );

      setResponse(res.data);
    } catch (err) {
      let errorMsg = 'Error processing query';
      if (err.code === 'ECONNABORTED') {
        errorMsg = 'Request timeout. Please try again.';
      } else if (err.response) {
        errorMsg = err.response.data.error || errorMsg;
      } else if (err.request) {
        errorMsg = 'Cannot connect to server. Make sure backend is running.';
      }
      setError(errorMsg);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="chat-container">
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Ask about flight bookings..."
          disabled={loading}
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Processing...' : 'Ask'}
        </button>
      </form>

      {loading && <div className="loading">Processing your query...</div>}

      {error && (
        <div className="error">
          {error}
          {error.includes('connect to server') && (
            <div className="server-tip">
              Ensure the backend server is running on port 5000
            </div>
          )}
        </div>
      )}

      {response && <VisualizationRenderer response={response} />}
    </div>
  );
}
