import React, { useState } from 'react';
import axios from 'axios';
import VisualizationRenderer from './VisualizationRenderer';

export default function ChatInterface() {
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  async function handleSubmit(e) {
    e.preventDefault();
    if (!query.trim()) return;

    setLoading(true);
    setError(null);
    setResponse(null);

    try {
      const res = await axios.post('http://localhost:5000/api/query', { 
        query: query.trim() 
      }, {
        timeout: 30000
      });

      if (res.data && typeof res.data.type !== 'undefined' && res.data.data !== undefined) {
        setResponse(res.data);
      } else {
        throw new Error('Invalid response format from server');
      }
    } catch (err) {
      const errorMessage = err.response?.data?.error || 
                          err.message || 
                          'Error processing your query';
      setError(errorMessage);
      console.error('Query error:', err);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div>
      <form onSubmit={handleSubmit} style={{ marginBottom: 20 }}>
        <input
          type="text"
          placeholder="Ask about flight bookings..."
          value={query}
          onChange={e => setQuery(e.target.value)}
          style={{ 
            width: '80%', 
            padding: '12px', 
            fontSize: '1rem',
            border: '1px solid #ddd',
            borderRadius: '4px'
          }}
          disabled={loading}
        />
        <button 
          type="submit" 
          style={{ 
            padding: '12px 20px', 
            marginLeft: 10,
            backgroundColor: '#4CAF50',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
          disabled={loading}
        >
          {loading ? 'Processing...' : 'Ask'}
        </button>
      </form>

      {loading && (
        <div style={{ 
          padding: '15px', 
          margin: '10px 0', 
          background: '#f8f9fa', 
          borderRadius: '4px'
        }}>
          Processing your query...
        </div>
      )}

      {error && (
        <div style={{ 
          color: 'red', 
          padding: '10px',
          margin: '10px 0',
          border: '1px solid #ffcccc',
          borderRadius: '4px'
        }}>
          {error}
        </div>
      )}

      {response && <VisualizationRenderer response={response} />}
    </div>
  );
}
