import React from 'react';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';

// Helper functions
const isDateColumn = (data, colName) => {
  if (!data.length) return false;
  const sampleValue = data[0][colName];
  return (
    (typeof sampleValue === 'string' && sampleValue.match(/\d{4}-\d{2}-\d{2}/)) ||
    sampleValue instanceof Date
  );
};

const detectChartType = (data) => {
  if (!data || !data.length) return null;

  const columns = Object.keys(data[0]);
  if (columns.length !== 2) return null;

  if (isDateColumn(data, columns[0])) return 'line';
  if (columns.some(col => col.toLowerCase().includes('count'))) return 'bar';
  return 'pie';
};

const transformDataForChart = (data, chartType) => {
  if (!data.length) return [];

  const columns = Object.keys(data[0]);
  const [labelCol, valueCol] = columns;

  return data.map(row => {
    const value = parseFloat(row[valueCol]) || 0;
    if (chartType === 'line') {
      return {
        x: new Date(row[labelCol]).getTime(),
        y: value,
        name: new Date(row[labelCol]).toLocaleDateString()
      };
    }
    return {
      name: row[labelCol],
      y: value
    };
  });
};

const getChartOptions = (chartType, data) => {
  const baseOptions = {
    title: { text: 'Query Results Visualization' },
    accessibility: { enabled: false },
    credits: { enabled: false }
  };

  switch (chartType) {
    case 'pie':
      return {
        ...baseOptions,
        chart: { type: 'pie' },
        plotOptions: {
          pie: {
            allowPointSelect: true,
            cursor: 'pointer',
            dataLabels: {
              enabled: true,
              format: '<b>{point.name}</b>: {point.y}'
            }
          }
        },
        series: [{ data }]
      };
    case 'bar':
      return {
        ...baseOptions,
        chart: { type: 'column' },
        xAxis: { type: 'category' },
        series: [{ data }]
      };
    case 'line':
      return {
        ...baseOptions,
        chart: { type: 'line' },
        xAxis: { type: 'datetime' },
        series: [{ data }]
      };
    default:
      return baseOptions;
  }
};

export default function VisualizationRenderer({ response }) {
  const { type, data } = response || {};

  if (type === 'string') {
    return (
      <div className="response-string">
        {data}
      </div>
    );
  }

  if (type === 'table') {
    if (!Array.isArray(data) ){
      return <div className="error">Invalid table data format</div>;
    }

    const chartType = detectChartType(data);
    const chartData = chartType ? transformDataForChart(data, chartType) : null;

    return (
      <div>
        {chartType && chartData && (
          <div style={{ marginBottom: '2rem' }}>
            <HighchartsReact
              highcharts={Highcharts}
              options={getChartOptions(chartType, chartData)}
            />
          </div>
        )}

        <div className="response-table">
          <table>
            <thead>
              <tr>
                {data.length > 0 && Object.keys(data[0]).map(col => (
                  <th key={col}>{col}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {data.map((row, i) => (
                <tr key={i}>
                  {Object.values(row).map((val, j) => (
                    <td key={j}>{val?.toString() || 'â€”'}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  }

  return <div className="error">Unsupported response type: {type}</div>;
}
