import React, { useState, useEffect } from 'react';
import axios from 'axios';
import VisualizationRenderer from './VisualizationRenderer';
import flightFacts from './flightFacts';

export default function ChatInterface() {
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [currentFact, setCurrentFact] = useState('');
  const [history, setHistory] = useState(() => {
    const saved = localStorage.getItem('queryHistory');
    return saved ? JSON.parse(saved) : [];
  });

  const getRandomFact = () => {
    const randomIndex = Math.floor(Math.random() * flightFacts.length);
    return flightFacts[randomIndex];
  };

  useEffect(() => {
    localStorage.setItem('queryHistory', JSON.stringify(history));
  }, [history]);

  async function handleSubmit(e) {
    e.preventDefault();
    if (!query.trim()) return;

    setLoading(true);
    setError(null);
    setCurrentFact(getRandomFact());

    try {
      const res = await axios.post('http://localhost:5000/api/query', {
        query: query.trim()
      }, {
        timeout: 60000
      });

      const result = { ...res.data, query: query.trim() };
      setResponse(result);
      setHistory(prev => [...prev, {
        query: query.trim(),
        response: result
      }]);
    } catch (err) {
      setError(
        err.response?.data?.error ||
        err.message ||
        'Error processing your query'
      );
      console.error('Query error:', err);
    } finally {
      setLoading(false);
      setCurrentFact('');
    }
  }
  const clearHistory = () => {
    setHistory([]);
    localStorage.removeItem('queryHistory');
  };
  return (
    <div style={{ maxWidth: 900, margin: 'auto', padding: 20 }}>

      <form onSubmit={handleSubmit} style={{ display: 'flex', marginBottom: 20 }}>
        <input
          type="text"
          placeholder="Ask about flight bookings..."
          value={query}
          onChange={e => setQuery(e.target.value)}
          style={{
            flex: 1,
            padding: '12px',
            fontSize: '1rem',
            border: '1px solid #ddd',
            borderRadius: '4px',
            minWidth: '300px' // Ensures minimum width
          }}
          disabled={loading}
        />
        <button
          type="submit"
          disabled={loading}
          style={{
            marginLeft: 10,
            padding: '12px 20px',
            backgroundColor: '#4CAF50',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: loading ? 'not-allowed' : 'pointer',
            fontSize: '1rem'
          }}
        >
          {loading ? 'Processing...' : 'Ask'}
        </button>
      </form>

      {loading && (
        <div style={{
          padding: '15px',
          margin: '10px 0',
          background: '#f0f0f0',
          borderRadius: '4px',
          textAlign: 'center'
        }}>
          <div style={{ 
            display: 'inline-block',
            width: '20px',
            height: '20px',
            border: '3px solid rgba(0,0,0,.1)',
            borderLeftColor: '#4CAF50',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite',
            marginBottom: '10px'
          }}></div>
          {currentFact && (
            <div style={{
              fontStyle: 'italic',
              color: '#666',
              marginTop: '10px'
            }}>
              ✈️ Did you know? {currentFact}
            </div>
          )}
        </div>
      )}
      {history.length > 0 && (
        <div style={{ 
          marginTop: '3rem',
          borderTop: '1px solid #ddd',
          paddingTop: '2rem',
          position: 'relative' // Added for absolute positioning of clear button
        }}>
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            marginBottom: '1rem'
          }}>
            <h3>Previous Queries</h3>
            <button
              onClick={clearHistory}
              style={{
                padding: '8px 16px',
                backgroundColor: '#f8f9fa',
                color: '#e74c3c',
                border: '1px solid #e74c3c',
                borderRadius: '4px',
                cursor: 'pointer',
                fontSize: '0.9rem',
                transition: 'all 0.2s',
                ':hover': {
                  backgroundColor: '#e74c3c',
                  color: 'white'
                }
              }}
            >
              Clear History
            </button>
          </div>

          {history.slice().reverse().map((item, index) => (
            <div key={index} style={{ 
              marginBottom: '2rem',
              padding: '1.5rem',
              background: '#f8f9fa',
              borderRadius: '6px'
            }}>
              <div style={{ 
                fontWeight: '600',
                color: '#3a0ca3',
                marginBottom: '1rem'
              }}>
                Q: {item.query}
              </div>
              <VisualizationRenderer response={item.response} />
            </div>
          ))}
        </div>
      )}
      {error && (
        <div style={{
          color: 'white',
          backgroundColor: '#e74c3c',
          padding: '10px',
          borderRadius: '4px',
          margin: '10px 0'
        }}>
          {error}
        </div>
      )}

      {response && <VisualizationRenderer response={response} />}

      {history.length > 0 && (
        <div style={{ 
          marginTop: '3rem',
          borderTop: '1px solid #ddd',
          paddingTop: '2rem'
        }}>
          <h3>Previous Queries</h3>
          {history.slice().reverse().map((item, index) => (
            <div key={index} style={{ 
              marginBottom: '2rem',
              padding: '1.5rem',
              background: '#f8f9fa',
              borderRadius: '6px'
            }}>
              <div style={{ 
                fontWeight: '600',
                color: '#3a0ca3',
                marginBottom: '1rem'
              }}>
                Q: {item.query}
              </div>
              <VisualizationRenderer response={item.response} />
            </div>
          ))}
        </div>
      )}

      <style>{`
        @keyframes spin {
          to { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
}
