import React, { useState } from 'react';
import axios from 'axios';
import VisualizationRenderer from './VisualizationRenderer';

export default function ChatInterface() {
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  async function handleSubmit(e) {
    e.preventDefault();
    if (!query.trim()) return;
    setLoading(true);
    setError(null);
    setResponse(null);

    try {
      const res = await axios.post('http://localhost:5000/api/query', { query });
      setResponse(res.data);
    } catch (err) {
      setError(err.message || 'Error querying backend');
    } finally {
      setLoading(false);
    }
  }

  return (
    <div>
      <form onSubmit={handleSubmit} style={{ marginBottom: 20 }}>
        <input
          type="text"
          placeholder="Ask about flight bookings..."
          value={query}
          onChange={e => setQuery(e.target.value)}
          style={{ width: '80%', padding: '10px', fontSize: '1rem' }}
        />
        <button type="submit" style={{ padding: '10px 15px', marginLeft: 10 }}>
          Ask
        </button>
      </form>

      {loading && <div>Loading response...</div>}
      {error && <div style={{ color: 'red' }}>{error}</div>}
      {response && <VisualizationRenderer response={response} />}
    </div>
  );
}
