import React, { useState } from 'react';
import axios from 'axios';
import VisualizationRenderer from './VisualizationRenderer';

export default function ChatInterface() {
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [history, setHistory] = useState([]);

  async function handleSubmit(e) {
    e.preventDefault();
    if (!query.trim()) return;

    setLoading(true);
    setError(null);

    try {
      const res = await axios.post('http://localhost:5000/api/query', {
        query: query.trim()
      }, {
        timeout: 60000
      });

      setResponse(res.data);
      setHistory(prev => [...prev, {
        query: query.trim(),
        response: res.data
      }]);
    } catch (err) {
      setError(
        err.response?.data?.error ||
        err.message ||
        'Error processing your query.'
      );
      console.error('Query error:', err);
    } finally {
      setLoading(false);
      setQuery('');
    }
  }

  return (
    <div className="chat-container">
      <h2>Flight Data Query</h2>

      <form onSubmit={handleSubmit} className="query-form">
        <input
          type="text"
          placeholder="Ask about flight bookings (e.g., 'Show bookings by airline')"
          value={query}
          onChange={e => setQuery(e.target.value)}
          disabled={loading}
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Processing...' : 'Ask'}
        </button>
      </form>

      {loading && <div className="loading-indicator">Processing your query...</div>}

      {error && <div className="error-message">{error}</div>}

      {response && (
        <div className="response-container">
          <VisualizationRenderer response={response} />
        </div>
      )}

      {history.length > 0 && (
        <div className="query-history">
          <h3>Query History</h3>
          {history.map((item, index) => (
            <div key={index} className="history-item">
              <div className="history-query">Q: {item.query}</div>
              <VisualizationRenderer response={item.response} />
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
