import React, { useState } from 'react';
import axios from 'axios';
import VisualizationRenderer from './VisualizationRenderer';
import flightFacts from './flightFacts';

export default function ChatInterface() {
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [currentFact, setCurrentFact] = useState('');

  const getRandomFact = () => {
    const randomIndex = Math.floor(Math.random() * flightFacts.length);
    return flightFacts[randomIndex];
  };

  async function handleSubmit(e) {
    e.preventDefault();
    if (!query.trim()) return;

    setLoading(true);
    setError(null);
    setResponse(null);
    setCurrentFact(getRandomFact());

    try {
      const res = await axios.post('http://localhost:5000/api/query', {
        query: query.trim()
      }, {
        timeout: 60000
      });

      setResponse(res.data);
    } catch (err) {
      setError(
        err.response?.data?.error ||
        err.message ||
        'Error processing your query.'
      );
      console.error('Query error:', err);
    } finally {
      setLoading(false);
      setCurrentFact('');
    }
  }

  return (
    <div style={{ maxWidth: 900, margin: 'auto', padding: 20 }}>
      <form onSubmit={handleSubmit} style={{ display: 'flex', marginBottom: 20 }}>
        <input
          type="text"
          placeholder="Ask about flight bookings..."
          value={query}
          onChange={e => setQuery(e.target.value)}
          style={{
            flex: 1,
            padding: '12px',
            fontSize: '1rem',
            border: '1px solid #ddd',
            borderRadius: '4px'
          }}
          disabled={loading}
        />
        <button
          type="submit"
          disabled={loading}
          style={{
            marginLeft: 10,
            padding: '12px 20px',
            backgroundColor: '#4CAF50',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: loading ? 'not-allowed' : 'pointer'
          }}
        >
          {loading ? 'Processing…' : 'Ask'}
        </button>
      </form>

      {loading && (
        <div style={{
          padding: '15px',
          margin: '10px 0',
          background: '#f0f0f0',
          borderRadius: '4px',
          textAlign: 'center'
        }}>
          <div style={{ marginBottom: '10px' }}>
            <div className="loading-spinner" style={{
              display: 'inline-block',
              width: '20px',
              height: '20px',
              border: '3px solid rgba(0,0,0,.1)',
              borderLeftColor: '#4CAF50',
              borderRadius: '50%',
              animation: 'spin 1s linear infinite'
            }}></div>
          </div>
          {currentFact && (
            <div style={{
              fontStyle: 'italic',
              color: '#666',
              marginTop: '10px'
            }}>
              ✈️ Fun Fact: {currentFact}
            </div>
          )}
        </div>
      )}

      {error && (
        <div style={{
          color: 'white',
          backgroundColor: '#e74c3c',
          padding: '10px',
          borderRadius: '4px',
          margin: '10px 0'
        }}>
          {error}
        </div>
      )}

      {response && (
        <VisualizationRenderer response={response} />
      )}

      <style>{`
        @keyframes spin {
          to { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
}
