import React, { useState, useEffect } from 'react';
import axios from 'axios';
import VisualizationRenderer from './VisualizationRenderer';
import flightFacts from './flightFacts';

export default function ChatInterface() {
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [currentFact, setCurrentFact] = useState('');
  const [history, setHistory] = useState(() => {
    const saved = localStorage.getItem('queryHistory');
    return saved ? JSON.parse(saved) : [];
  });

  const getRandomFact = () => flightFacts[Math.floor(Math.random() * flightFacts.length)];

  useEffect(() => {
    localStorage.setItem('queryHistory', JSON.stringify(history));
  }, [history]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;

    setLoading(true);
    setError(null);
    setCurrentFact(getRandomFact());

    try {
      // Check backend health first
      await axios.get('http://localhost:5000/api/health', { timeout: 3000 });

      const res = await axios.post('http://localhost:5000/api/query', 
        { query: query.trim() },
        { timeout: 30000 }
      );

      const result = { ...res.data, query: query.trim() };
      setResponse(result);
      setHistory(prev => [...prev, { query: query.trim(), response: result }]);
    } catch (err) {
      setError(
        err.response?.data?.error ||
        (err.code === 'ECONNABORTED' ? 'Request timeout' : 
         err.message.includes('Network Error') ? 'Cannot connect to server' : 
         'Error processing query')
      );
    } finally {
      setLoading(false);
      setCurrentFact('');
    }
  };

  return (
    <div className="chat-interface">
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Ask about flight bookings..."
          disabled={loading}
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Processing...' : 'Ask'}
        </button>
      </form>

      {loading && (
        <div className="loading-indicator">
          <div className="spinner"></div>
          {currentFact && <div className="fun-fact">✈️ Did you know? {currentFact}</div>}
        </div>
      )}

      {error && (
        <div className="error-message">
          {error}
          {error.includes('connect to server') && (
            <div className="server-tip">Make sure backend is running on port 5000</div>
          )}
        </div>
      )}

      {response && <VisualizationRenderer response={response} />}

      {history.length > 0 && (
        <div className="query-history">
          <h3>Previous Queries</h3>
          {history.slice().reverse().map((item, i) => (
            <div key={i} className="history-item">
              <div className="history-query">Q: {item.query}</div>
              <VisualizationRenderer response={item.response} />
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
