// src/components/VisualizationRenderer.jsx
import React from 'react';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';

export default function VisualizationRenderer({ response }) {
  const { type, data } = response;

  // Basic string output
  if (type === 'string') {
    return (
      <div style={{
        whiteSpace: 'pre-wrap',
        backgroundColor: '#f8f9fa',
        border: '1px solid #ddd',
        borderRadius: '4px',
        padding: '15px',
        margin: '10px 0'
      }}>
        {data}
      </div>
    );
  }

  // Table output
  if (type === 'table') {
    if (!Array.isArray(data)) {
      return <div style={{ color: 'red' }}>Table data must be an array.</div>;
    }
    const headers = data.length
      ? [...new Set(data.flatMap(Object.keys))]
      : [];

    return (
      <div style={{
        overflowX: 'auto',
        margin: '20px 0',
        boxShadow: '0 2px 5px rgba(0,0,0,0.1)'
      }}>
        <table style={{
          width: '100%',
          borderCollapse: 'collapse',
          fontSize: '0.9rem'
        }}>
          <thead>
            <tr style={{ backgroundColor: '#f5f5f5', textAlign: 'left' }}>
              {headers.map(col => (
                <th key={col} style={{
                  padding: '12px',
                  border: '1px solid #ddd',
                  fontWeight: 'bold'
                }}>
                  {col}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {data.map((row, i) => (
              <tr key={i} style={{
                borderBottom: '1px solid #ddd'
              }}>
                {headers.map(col => (
                  <td key={col} style={{
                    padding: '12px',
                    border: '1px solid #ddd'
                  }}>
                    {row[col] ?? '—'}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }

  // Charts (pie, bar, timeseries)
  const commonSeries = {
    name: 'Value',
    data: data.map(item => {
      const value = typeof item.value === 'string'
        ? parseFloat(item.value)
        : item.value;
      return type === 'timeseries'
        ? [new Date(item.date).getTime(), value]
        : { name: item.name, y: value };
    })
  };

  let options = {};
  switch (type) {
    case 'pie':
      options = {
        chart: { type: 'pie' },
        title: { text: 'Distribution' },
        series: [commonSeries],
        plotOptions: {
          pie: {
            allowPointSelect: true,
            cursor: 'pointer',
            dataLabels: { enabled: true, format: '<b>{point.name}</b>: {point.percentage:.1f} %' }
          }
        }
      };
      break;

    case 'bar':
      options = {
        chart: { type: 'column' },
        title: { text: 'Comparison' },
        xAxis: { type: 'category', title: { text: 'Category' } },
        yAxis: { title: { text: 'Value' } },
        series: [commonSeries],
        plotOptions: {
          column: { colorByPoint: true }
        }
      };
      break;

    case 'timeseries':
      options = {
        chart: { type: 'line' },
        title: { text: 'Trend Over Time' },
        xAxis: { type: 'datetime', title: { text: 'Date' } },
        yAxis: { title: { text: 'Value' } },
        series: [{
          name: 'Value',
          data: commonSeries.data
        }],
        tooltip: {
          xDateFormat: '%Y-%m-%d',
          pointFormat: '<br/>Value: <b>{point.y}</b>'
        }
      };
      break;

    default:
      return (
        <div style={{
          color: '#e67e22',
          padding: '15px',
          border: '1px solid #f1c40f',
          borderRadius: '4px',
          margin: '10px 0'
        }}>
          Unsupported visualization type: <strong>{type}</strong>
        </div>
      );
  }

  return (
    <div style={{ margin: '20px 0' }}>
      <HighchartsReact highcharts={Highcharts} options={options} />
    </div>
  );
}
