import React from 'react';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';

const detectChartType = (data, query) => {
  if (!data || !data.length) return 'table';

  const columns = Object.keys(data[0]);
  if (columns.length !== 2) return 'table';

  // Time series detection
  const isDate = columns.some(col => 
    col.toLowerCase().includes('date') || 
    col.toLowerCase().includes('time')
  );
  if (isDate) return 'line';

  // Comparison detection
  if (query.includes('compare')) return 'bar';

  // Distribution detection
  if (query.includes('distribution') || query.includes('percentage')) return 'pie';

  // Default for two columns
  return 'bar';
};

const prepareChartData = (data, chartType) => {
  const columns = Object.keys(data[0]);
  const [labelCol, valueCol] = columns;

  return data.map(row => {
    const value = parseFloat(row[valueCol]) || 0;
    if (chartType === 'line') {
      return {
        x: new Date(row[labelCol]).getTime(),
        y: value,
        name: new Date(row[labelCol]).toLocaleDateString()
      };
    }
    return {
      name: row[labelCol],
      y: value
    };
  });
};

const getChartOptions = (chartType, data, query) => {
  const baseOptions = {
    title: { text: 'Query Results' },
    accessibility: { enabled: false },
    credits: { enabled: false },
    chart: { 
      type: chartType === 'line' ? 'line' : 
            chartType === 'pie' ? 'pie' : 'column',
      backgroundColor: 'transparent'
    },
    plotOptions: {
      series: {
        borderRadius: chartType === 'column' ? 4 : 0
      }
    }
  };

  if (chartType === 'pie') {
    return {
      ...baseOptions,
      plotOptions: {
        pie: {
          allowPointSelect: true,
          cursor: 'pointer',
          dataLabels: {
            enabled: true,
            format: '<b>{point.name}</b>: {point.y}'
          }
        }
      },
      series: [{ data }]
    };
  }

  if (chartType === 'line') {
    return {
      ...baseOptions,
      xAxis: { type: 'datetime' },
      series: [{ data }]
    };
  }

  return {
    ...baseOptions,
    xAxis: { type: 'category' },
    series: [{ data }]
  };
};

export default function VisualizationRenderer({ response }) {
  const { type, data, sql } = response || {};

  if (type === 'string') {
    return (
      <div className="response-string">
        {data}
        {sql && <div className="sql-debug">SQL: {sql}</div>}
      </div>
    );
  }

  if (type === 'table') {
    if (!Array.isArray(data)) {
      return <div className="error">Invalid table data</div>;
    }

    const chartType = detectChartType(data, response.query);
    const showChart = data.length > 0 && data.length < 1000 && chartType !== 'table';
    const chartData = showChart ? prepareChartData(data, chartType) : null;

    return (
      <div className="response-container">
        {showChart && (
          <div className="chart-container">
            <HighchartsReact
              highcharts={Highcharts}
              options={getChartOptions(chartType, chartData, response.query)}
            />
          </div>
        )}

        <div className="response-table">
          <table>
            <thead>
              <tr>
                {data.length > 0 && Object.keys(data[0]).map(col => (
                  <th key={col}>{col}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {data.map((row, i) => (
                <tr key={i}>
                  {Object.values(row).map((val, j) => (
                    <td key={j}>{val?.toString() || '—'}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {sql && <div className="sql-debug">SQL: {sql}</div>}
      </div>
    );
  }

  return <div className="error">Unsupported response type: {type}</div>;
}
